'use strict';

/*jslint indent: 2 */

var fs = require('fs');
var path = require('path');

exports.walk = function (root,  addFile) {
    var files, stats;

    files = fs.readdirSync(root);

    stats = files.map(function (name) {
      return fs.statSync(path.join(root, name));
    });

    stats.forEach(function (stat, i) {
      var file = files[i];
      if (stat.isFile()) {
        addFile(file);
      }
    });
};
