'use strict';

/*jslint indent: 2 */

var trace = process.env.TRACE;

exports.log = console.log;

exports.error = console.error;

exports.setTrace = function (value) {
  trace = value;
};

exports.trace = function () {
  if (trace) {
    console.log.apply(this, Array.prototype.slice.call(arguments));
  }
};
