/**
 * RpcArray
 */
'use strict';
module.exports = RpcArray;

var RpcObject = require('./RpcObject.js');
var util = require('util');
function RpcArray()
{
	RpcObject.call(this, "array");
	this.value = [];
}

util.inherits(RpcArray, RpcObject); 

RpcArray.prototype.isArray = function()
{
	return true;
};

RpcArray.prototype.asJsonElement = function()
{
	var jsonObject = [];

	for (var i = 0; i < this.value.length; i++)
	{
		jsonObject[i] = this.value[i].toJsonObject();
	}

	return jsonObject;
};


