/**
 * RpcBinary
 */
'use strict';
module.exports = RpcBinary;

var RpcPrimitive = require('./RpcPrimitive.js');
var Base64 = require('./Base64.js');
var util = require('util');
function RpcBinary(value)
{
	RpcPrimitive.call(this, "binary");
	this.value = value;
}

util.inherits(RpcBinary, RpcPrimitive); 

RpcBinary.prototype.asJsonElement = function()
{
	return Base64.encode(this.value);
};
