/**
 * RpcInteger
 */
'use strict';
module.exports = RpcInteger;

var RpcPrimitive = require('./RpcPrimitive.js');
var util = require('util');
function RpcInteger(value)
{
	RpcPrimitive.call(this, "integer");
	this.value = value;
}

util.inherits(RpcInteger, RpcPrimitive); 

RpcInteger.prototype.asJsonElement = function()
{
	return this.value;
};