'use strict';
module.exports= RpcMessage;

function RpcMessage()
{
	this.method = null
	this.params = [];
}

RpcMessage.prototype.toJsonObject = function()
{
	var jsonParams = [];

	for (var i = 0; i < this.params.length; i++)
	{
		jsonParams[i] = this.params[i].toJsonObject();
	}

	var jsonMessage = {};

	jsonMessage["method"] = this.method;
	jsonMessage["params"] = jsonParams;

	return jsonMessage;
};