/**
 * RpcNull
 */
'use strict';
module.exports = RpcNull;

var RpcObject = require('./RpcObject.js');
var util = require('util');
function RpcNull()
{
	RpcObject.call(this, "null");
}

util.inherits(RpcNull, RpcObject); 

RpcNull.prototype.isNull = function()
{
	return true;
};

RpcNull.prototype.asJsonElement = function()
{
	return null;
};