/**
 * RpcShort
 */
'use strict';
module.exports = RpcShort;
var RpcPrimitive = require('./RpcPrimitive.js');
var util = require('util');
function RpcShort(value)
{
	RpcPrimitive.call(this, "short");
	this.value = value;
}

util.inherits(RpcShort, RpcPrimitive); 

RpcShort.prototype.asJsonElement = function()
{
	return this.value;
};
