
/**
 * RpcString
 */
'use strict';
module.exports = RpcString;

var RpcPrimitive = require('./RpcPrimitive.js');
var util = require('util');
function RpcString(value)
{
	RpcPrimitive.call(this, "string");
	this.value = value;
}

util.inherits(RpcString, RpcPrimitive); 

RpcString.prototype.asJsonElement = function()
{
	return this.value;
};
