/**
 * RpcStruct
 */
'use strict';
module.exports = RpcStruct; 

var RpcObject = require('./RpcObject.js');
var util = require('util');
function RpcStruct()
{
	RpcObject.call(this, "struct");
	this.value = {};
}

util.inherits(RpcStruct, RpcObject); // Inherit the methods of RpcObject

RpcStruct.prototype.isStruct = function()
{
	return true;
};

RpcStruct.prototype.asJsonElement = function()
{
	var jsonObject = {};

	for (var key in this.value)
	{
		if (this.value.hasOwnProperty(key))
		{
			var rpcObject = this.value[key];
			jsonObject[key] = rpcObject.toJsonObject();
		}
	}

	return jsonObject;
};