/**
 * Workspace
 */
'use strict';
module.exports= Workspace;

var RpcStruct = require('./RpcStruct.js');
var RpcString = require('./RpcString.js');
var Cluster = require('./Cluster.js');
function Workspace() {}

Workspace.add = function(rpcContext, workspaceName, cb)
{
	var arg = new RpcStruct();
	arg.value["workspace-name"] = new RpcString(workspaceName);
	Cluster.addWorkspace(rpcContext, arg, cb);
};

Workspace.remove = function(rpcContext, workspaceName, cb)
{
	var arg = new RpcStruct();
	arg.value["workspace-name"] = new RpcString(workspaceName);
	Cluster.removeWorkspace(rpcContext, arg, cb);
};

Workspace.get = function(rpcContext, workspaceName, cb)
{
	var arg = new RpcStruct();

	if (workspaceName != null)
	{
		arg.value["workspace-name"] = new RpcString(workspaceName);
	}

	Cluster.getWorkspaces(rpcContext, arg, cb);
};
