'use strict';

class StringUtils {
    static fixedLength(str, length) {
        if (str.length <= length) {
            return str + (new Array(length - str.length + 1)).join(' ');
        } else if (length > 3) {
            return str.substr(0, length - 3) + '...';
        } else {
            return str.substr(0, length);
        }
    }
}

module.exports = StringUtils;