'use strict';

var xsrfHeandler = require('./xsrf-token-handler');

module.exports = function xsrfCheck(req, res, next) {
  if (!xsrfHeandler.isTokenRequired(req)) {
    return next();
  }
  if (xsrfHeandler.isTokenFetchRequest(req)) {
    xsrfHeandler.getToken(req, function (err, token) {
      if (err) {
        var logger = req.loggingContext.getLogger('/XSRF-Handler');
        var message = 'Failed to get new xsrf token';
        logger.error(message, err);
        res.writeHead(500);
        res.end(message);
        return;
      }
      res.setHeader('x-csrf-token', token);
      return next();
    });
  } else {
    var result = xsrfHeandler.checkToken(req);
    if (result.isValid) {
      return next();
    }
    res.writeHead(403, { 'x-csrf-token': 'Required' });
    res.end(result.details);
  }
};
